var tempImageApi = '';

var classNamePrefix = 'book_ext_',
    singlePage = false,
    level = 0,
    imageScale = false,
    filterList = {};


function parseBdjson(data, iscale){
	var result = [];

    imageScale = iscale;

    if (data.c instanceof Array) {

        data.c.forEach(function(item){

            if(!item.hasOwnProperty('t')){
                return false;
            }
            singlePage = false;
            item.level = level++;
            var content = parseChild(item);
            level = 0;
            result.push(content);
        });

	}

    return result.join('');
}

function parseChild(data) {
    var args = arguments,
        fun,
        result = [];
    data.level = level++;
    if(!parseTagList[data.t]){return '';}
    if(data.c instanceof Array){
        data.c.forEach(function(item){
            if(item.c instanceof Array){
                result.push(parseChild(item));
            }else if(item.hasOwnProperty('t') && typeof parseTagList[item.t] === 'function'){
                result.push(parseTagList[item.t](item, true));
            }
        });
        data.c = result.join('');
        return parseTagList[data.t](data);
    }
    return parseTagList[data.t](data, true);

}

//自定义属性
var customAttr = {
    'href' : function(href){
        return {
            attr: ' data-href="' + href + '"',
            className: ' has-href'
        };
    }
};

function parseCustomAttr (data){
    if (data.hasOwnProperty('href')) {
        return customAttr.href(data.href);
    }

    return false;
}

function parseNormalTag(data, isEncode) {
    if (!data.hasOwnProperty('t')) {
        return '';
    }

    data.r = data.r || [];
    data.c = data.c || '';

    var isSingle = singlePage && data.level < singlePage,
        attr = '',
        tag, className, content, hasClass, custAttr;

    if(isSingle){
        data.t = 'div';
        data.r.push(classNamePrefix + 'single-page-wrap');
    }

    tag = htmlEscape(data.t);
    className = htmlEscape((data.r instanceof Array) ? data.r.join(' ') : data.r);
    content = data.c;
    hasClass = data.r.length;
    custAttr = parseCustomAttr(data);

    if(custAttr) {
        className += custAttr.className || '';
        attr += custAttr.attr || '';
        hasClass = true;
    }

    // if( isEncode && $.os.android && !singlePage ){
    //     content =  content.replace(/(?!^)\B(?=[^\x00-\xff])/g,' ');
    //     content = content.replace(/([\x00-\xff][\x00-\xff]+)/g,'<span class="nospace">$1</span>');
    //     className += ' text-justify';
    //     hasClass = true;
    // }

    if(tag == 'h3') {
        content = content.replace('【', '').replace('】', '');
    }

    if(tag == 'h4') {
        content = content.replace('【', '').replace('】', '');
        content = '<div class="leaf">' + content + '</div>';
    }

    return '<' + tag
        + (hasClass ? ' class="' + className + '"' : '')
        + (data.inlineStyle ? ' style="' + data.inlineStyle + '"' : '')
        + ' ' + attr + ' '
        + '>' + content + '</' + tag + '>';
}

function parseImgTag(data) {
    var imgUrl = tempImageApi  + data.src,
        width = parseInt(data.w, 10)|| 0,
        height = parseInt(data.h, 10)|| 0;

    if (filterList.hasOwnProperty('img')) {
        return filterList.img(imgUrl, width, height);
    }


    var onclick = 'function(){this.src=\'' + imgUrl + '\'}';
    // imgUrl = 'xx';

    return '<img src="' + imgUrl + '" '
        + (width > 0 ? ('width="' + width + 'px" ') : ' ')
        + (imageScale ? ('onclick="new FullImage($(this))" ') : ' ')
        + (height > 0 ? ('height="' + height + 'px"') : ' ')
        + '/>';
}

function parseBrTag(data) {
    var tag = 'br',
        className;

    if (data.hasOwnProperty('datatype')) {
        className = classNamePrefix + data.datatype;
        tag = 'span';
    }

    return '<' + tag + ' '
        + (className ? ('class="' + className + '"') : '')
        + '/>';
}


/**
 * 解析列表
 * @param  {Object}  data     代码的obj数据
 * @return {String}           解析的HTML字符串
 */
function parseSdivLists(data){
    var modifiedData = (function (data) {
        //去掉p标签
        var tmp = data.c.split('</p>');
        tmp.forEach(function (item, i) {
            tmp[i] = item.replace(/^<p[^.]+?>/, '');
        });
        tmp.pop();
        return tmp;
    })(data);
    var html = '';

        var listTypes = {
            'decimal' : 'ol',
            'lower-alpha' : 'ol',
            'cjk-ideographic' : 'ol',
            'upper-roman' : 'ol',

            'dot' : 'ul',
            'disc' : 'ul' ,
            'square' : 'ul',
            'diamond' : 'ul'
        };
        var customIndexToString = {
            'cjk-ideographic' : function (num) {
                var cjk = {
                    '0': '零',
                    '1': '一',
                    '2': '二',
                    '3': '三',
                    '4': '四',
                    '5': '五',
                    '6': '六',
                    '7': '七',
                    '8': '八',
                    '9': '九',
                    '10': '十',
                    '100': '百',
                    '1000': '千',
                    '10000': '万'
                };
                var str = [], tmp='';
                num += '';
                var length = num.length;
                for(var i = 0; i< length; i++){
                    if(num[i]===0){
                        if(num[i+1] && num[i+1] !== 0 ){
                            tmp = cjk['0'];
                        }
                    }else{
                        if(!(length==2 && num[0]==1 && i === 0)){
                            tmp = cjk[num[i]];
                        }
                        switch(length-i-1){
                        case 1:
                            tmp += cjk['10'];
                            break;
                        case 2:
                            tmp += cjk['100'];
                            break;
                        case 3:
                            tmp += cjk['1000'];
                            break;
                        case 4:
                            tmp += cjk['10000'];
                            break;
                        }
                    }
                    str.push(tmp);
                    tmp = '';
                }
                str.push('. ');
                return str.join('');
            }
        };

        var symbolList = {
            // 'dot': '/assets/images/icon-list-disc.png',
            'dot': 'data:image/png;base64,',
            // 'disc': '/assets/images/icon-list-disc.png',
            'disc': 'data:image/png;base64,',
            // 'square': '/assets/images/icon-list-diamond.png',
            'square': 'data:image/png;base64,',
            // 'diamond': '/assets/images/icon-list-diamond.png'
            'diamond': 'data:image/png;base64,'
        };

        var liColor = function (str) {
            var result = str.match(/<span class="(.*)(ext_color_[^.(?= )]+)/);
            return result? result[2]:  '';
        };

    modifiedData.forEach(function (item, i) {
            var color = liColor(item);
            var span = item;
            if(span.indexOf('<span')!==0){
                span = '<span>'+item+'</span>';
            }
            if(symbolList[data.listtype]){
                span = '<img src="'+symbolList[data.listtype]+'" class="list-'+data.listtype+'-icon" alt="" />'+span;
            }
            if(customIndexToString[data.listtype]){
                span = '<span class="list-'+data.listtype+'-icon '+ (color||'')+'">'+customIndexToString[data.listtype](i+1)+'</span>'+span;
            }
            html += '<li class="list-'+data.listtype+(color?' '+color : '')+'"><p>'+span+'</p></li>';
        });
        return '<'+listTypes[data.listtype]+' class="custom-'+data.listtype+'">'+html+'</'+listTypes[data.listtype]+'>';

}

function parseSdiv(data){
    sendXLog('sdiv');
    if(data.datatype == 'list'){
        return parseSdivLists(data);
    }

    return '<div class="ext_code_wrap"><div class="code-container code-theme-default">'
        + data.c + '</div><span class="btn-code-zoom"></span></div>';
}

function parseObj(data) {
    var result = '';
    if(!data.hasOwnProperty('datatype') || !parseObjList.hasOwnProperty(data.datatype)){
        return result;
    }

    result = parseObjList[data.datatype](data);

    return result;
}

function parseTableTag(data) {
    sendXLog('table');
    //if(data.hasOwnProperty('bg-img')) {
    //    return parseImgTag(data['bg-img']);
    //}

    return 'table';
}

function parseTrTag () {
    return 'tr';
}

function parseThTag () {
    return 'th';
}

var parseTagList = {
	'span': parseNormalTag,
    'h2': parseNormalTag,
    'h3': parseNormalTag,
    'h4': parseNormalTag,
    'p': parseNormalTag,
    'div': parseNormalTag,
    'img': parseImgTag,
    'br': parseBrTag,
    'obj': parseObj,
    'sdiv': parseSdiv,
    'table': parseNormalTag,
    'tr': parseNormalTag,
    'th': parseNormalTag,
    'td': parseNormalTag
};



function parseObjNote(data) {
    sendXLog('note');
    if (!data.hasOwnProperty('data')
        && !(data.data instanceof Array)
        && !data.data[0].hasOwnProperty('c')) {

        return '';
    }

    var className = classNamePrefix + 'note';

    return '<span class="' + className + '" data-note="' + htmlEscape(data.data[0].c) + '"></span>';
}

function parseObjPage(data){
    sendXLog('page');
    var bgImg;

    data.level = level++;
    singlePage = data.level;
    data.t="div";

    if(data.data[0] && data.data[0].t==='p'){
        data.c = data.data[0].c;
    }else{
        data.c = data.data;
    }

    data.r = data.r || [];
    data.r.push(classNamePrefix + 'single-page');
    bgImg = data['bg-img'];
    data.inlineStyle = bgImg ? 'background-image:url(' + tempImageApi + bgImg +')' : 'background-image:none';
    return parseChild(data);
}

function parseObjTextBox(data){
    data.t = 'div';
    data.c = data.data;
    data.r.push(classNamePrefix + 'textbox');

    return parseChild(data);
}

function parseMath(data){
    sendXLog('math');
    if (data.hasOwnProperty('data') && (data.data instanceof Array)) {
        return parseImgTag(data.data[0]);
    }

    return '';
}

var parseObjList = {
    'note': parseObjNote,
    'page' : parseObjPage,
    'textbox' : parseObjTextBox,
    'math': parseMath
};


